<div class="related">
    <section class="content-main-box">
        <header class="content-main-title mb-35">
            <div class="row align-items mb-20">
                <img alt="related" src="data:image/svg+xml;base64," />
                <span class="line-65"></span>
            </div>
            <h3 class="title mb-20">مطالب <span class="lightBlue">مرتبط</span></h3>
        </header>
        <!--article post box start-->
        <div class="row mb-10 related-posts">
            <?php
            $post_id = get_the_ID();
            $cat_ids = array();
            $categories = get_the_category($post_id);
            if (!empty($categories) && !is_wp_error($categories)):
                foreach ($categories as $category):
                    array_push($cat_ids, $category->term_id);
                endforeach;
            endif;
            $current_post_type = get_post_type($post_id);
            $query_args = array(
                'category__in' => $cat_ids,
                'post_type' => $current_post_type,
                'post__not_in' => array($post_id),
                'posts_per_page' => '4',
            );
            $related_cats_post = new WP_Query($query_args);
            if ($related_cats_post->have_posts()):
                while ($related_cats_post->have_posts()):
                    $related_cats_post->the_post(); ?>
                    <!--article start -->

                    <article class="column post-box">
                        <header class="post-header">
                            <figure class="post-image mb-10">
                                <?php the_post_thumbnail(); ?>
                            </figure>
                        </header>
                        <div class="post-content">
                            <div class="post-entry mb-10">
                                <h2 class="entry-title"><a rel="external" href="<?php the_permalink() ?>">
                                        <?php the_title(); ?>
                                    </a>
                                </h2>
                            </div>
                            <div class="post-excerpt mb-10">
                                <?php the_excerpt(); ?>
                            </div>
                            <div class="post-more">
                                <a href="<?php the_permalink() ?>">جهت مشاهده کلیک کنید</a>
                            </div>
                        </div>
                    </article>

                    <!--article end -->
                <?php endwhile;
                // Restore original Post Data
                wp_reset_postdata();
            endif;
            ?>
            <!--article post box end-->
    </section>
</div>